import React, { useState } from 'react';
import NavBar from '../components/Navbar/NavBar';
import Footer from '../components/Footer';
import axios from 'axios';
import { toast } from "react-toastify";
import { useContext } from "react";
import {  useNavigate, Navigate } from "react-router-dom";
import { Context } from '../index.js';

const Login = () => {


  const { isAuthenticated, setIsAuthenticated } = useContext(Context);

    const [email, setEmail] = useState('')
    const [password, setPassword] = useState("");
    const [confirmPassword, setConfirmPassword] = useState("");


  const navigateTo = useNavigate();



    const handleLogin = async (e) => {
        e.preventDefault();
        try {
          await axios
            .post(
              "http://localhost:4000/api/v1/user/login",
              { email, password, confirmPassword, role: "User" },
              {
                withCredentials: true,
                headers: { "Content-Type": "application/json" },
              }
            )
            .then((res) => {
              toast.success(res.data.message);
              setIsAuthenticated(true);
              navigateTo("/");
              setEmail("");
              setPassword("");
              setConfirmPassword("");
            });
        } catch (error) {
          toast.error(error.response.data.message);
        }
      };


  if (isAuthenticated) {
    return <Navigate to={"/"} />;
  }
    return (
        <>
            <div>
                <NavBar />
            </div>
            <div id='login' className="flex justify-center items-center mt-8 w-full bg-white py-12 lg:py-24">
                <div className="container mx-auto my-8 px-4 lg:px-20" data-aos="zoom-in">
                    <form onSubmit={handleLogin} id="loginProductForm">
                        <div className="w-full bg-white p-8 my-4 md:px-12 lg:w-9/12 lg:pl-20 lg:pr-40 mr-auto rounded-2xl shadow-2xl">
                            <div className="flex">
                                <h1 className="font-bold text-center lg:text-left text-blue-900 uppercase text-4xl">Login our products</h1>
                            </div>
                            <p>
                            The login functionality allows users to securely access their accounts by verifying their credentials. This process typically involves collecting user-provided data such as an email or username and a password, which are then validated against stored records.
                            </p>
                            <div className="my-4">
                                <div>
                                    <input 
                                        name="email"
                                        className="w-full bg-gray-100 text-gray-900 mt-2 p-3 rounded-lg focus:outline-none focus:shadow-outline"
                                        type="email" 
                                        placeholder="Email*"
                                        value={email}
                                        onChange={(e)=> setEmail(e.target.value)} 
                                    />
                                </div>
                                </div>
                                <div>
                                    <input
                                        name="password" 
                                        className="w-full bg-gray-100 text-gray-900 mt-2 p-3 rounded-lg focus:outline-none focus:shadow-outline"
                                        type="number" 
                                        placeholder="password*"
                                        value={password}
                                        onChange={(e)=> setPassword(e.target.value)}
                                    />
                                </div>

                            <input
                                name="confirmPassword" 
                                placeholder="confirmPassword*" 
                              className="w-full bg-gray-100 text-gray-900 mt-2 p-3 rounded-lg focus:outline-none focus:shadow-outline"
                              type="number"  
                              value={confirmPassword}
                                onChange={(e)=> setConfirmPassword(e.target.value)}
                            />

                        <div className="my-2 w-1/2 lg:w-2/4">
                            <button type="submit" id="submitBtn" className="uppercase text-sm font-bold tracking-wide bg-gray-500 hover:bg-blue-900 text-gray-100 p-3 rounded-lg w-full 
                                    focus:outline-none focus:shadow-outline">
                                Login
                            </button>
                        </div>
                    </div>
                    </form>

                </div>
            </div>
            <Footer />
        </>


    )
}

export default Login;
