import React, { useState, useContext } from 'react';
import NavBar from '../components/Navbar/NavBar';
import Footer from '../components/Footer';
import {useDocTitle} from '../components/CustomHook';
import axios from 'axios';
import { Navigate, useNavigate, Link } from 'react-router-dom';
import { Context } from '../index.js';
import {toast} from "react-hot-toast"

const DemoProduct = (props) => {

    useDocTitle('MLD | Molad e Konsult - Demo our products')


  const { isAuthenticated, setIsAuthenticated } = useContext(Context);

    const [email, setEmail] = useState('')
    const [password, setPassword] = useState('')
    const [confirmPassword, setConfirmPassword] = useState('')
    const [demoProducts, setDemoProducts ] = useState([])


  const navigateTo = useNavigate();



    const clearInput = () => {
        setEmail('')
        setPassword('')
        setConfirmPassword('')
    }

    const handleLogin = async (e) => {
        e.preventDefault();
        try {
          await axios
            .post(
              "http://localhost:4000/api/v1/user/login",
              { email, password, confirmPassword, role: "user" },
              {
                withCredentials: true,
                headers: { "Content-Type": "application/json" },
              }
            )
            .then((res) => {
              toast.success(res.data.message);
              setIsAuthenticated(true);
              navigateTo("/");
              setEmail("");
              setPassword("");
              setConfirmPassword("");
            });
        } catch (error) {
          toast.error(error.response.data.message);
        }
      };

      if (isAuthenticated) {
        return <Navigate to={"/"} />;
      }

    return (
        <>
            <div>
                <NavBar />
            </div>
            <div id='demo' className="flex justify-center items-center mt-8 w-full bg-white py-12 lg:py-24">
                <div className="container mx-auto my-8 px-4 lg:px-20" data-aos="zoom-in">
                    <form onSubmit={handleLogin} id="demoProductForm">
                        <div className="w-full bg-white p-8 my-4 md:px-12 lg:w-9/12 lg:pl-20 lg:pr-40 mr-auto rounded-2xl shadow-2xl">
                            <div className="flex">
                                <h1 className="font-bold text-center lg:text-left text-blue-900 uppercase text-4xl">Demo our products</h1>
                            </div>


                                <div>
                                    <input 
                                        name="email"
                                        className="w-full bg-gray-100 text-gray-900 mt-2 p-3 rounded-lg focus:outline-none focus:shadow-outline"
                                        type="email" 
                                        placeholder="Email*"
                                        value={email}
                                        onChange={(e)=> setEmail(e.target.value)}
                                    />
                                </div>
                                <div>
                                    <input
                                        name="password" 
                                        className="w-full bg-gray-100 text-gray-900 mt-2 p-3 rounded-lg focus:outline-none focus:shadow-outline"
                                        type="number" 
                                        placeholder="password*"
                                        value={password}
                                        onChange={(e)=> setPassword(e.target.value)}
                                    />
                                </div>

                            <input 
                                name="confirmPassword" 
                                placeholder="confirmPassword*" 
                                className="w-full bg-gray-100 text-gray-900 mt-2 p-3 rounded-lg focus:outline-none focus:shadow-outline"
                                type="number" 
                                value={confirmPassword}
                                onChange={(e)=> setConfirmPassword(e.target.value)}
                            ></input>
                           {/* Button */}
            <div className="flex justify-around mt-6">
              <button className="bg-pink-500 text-white rounded-md px-3 py-1 hover:bg-pink-700 duration-200">
                Login
              </button>
              <p className="text-black dark:text-black">
                Not registered?{" "}
                <Link
                  to="/signup"
                  className="underline text-blue-500 cursor-pointer"
                >
                  Signup
                </Link>{" "}
              </p>
            </div>
                    </div>
                    </form>
                </div>
            </div>
            <Footer />
        </>


    )
}

export default DemoProduct;
